mapper.select:
SELECT comm.cid, comm.ccont, comm.ctime, comm.cdel, comm.tid, comm.uid, users.uname, users.udesc
FROM comm, users
WHERE comm.uid = users.uid AND comm.tid = #{tid}

SELECT comm.cid, comm.ccont, comm.ctime, comm.cdel, comm.tid, comm.uid, users.uname, users.udesc
FROM comm, users
WHERE comm.uid = #{uid} AND users.uid = #{uid}

SELECT comm.cid, comm.ccont, comm.ctime, comm.cdel, comm.tid, comm.uid, users.uname, users.udesc
FROM comm, users
WHERE comm.uid = users.uid AND comm.cid = #{cid}
